  assign intr_test_kmac_err_we = addr_hit[2] & reg_we & ~wr_err;
  assign intr_test_kmac_err_wd = reg_wdata[2];

  assign cfg_kmac_en_we = addr_hit[3] & reg_we & ~wr_err;
  assign cfg_kmac_en_wd = reg_wdata[0];
  assign cfg_kmac_en_re = addr_hit[3] && reg_re;

  assign cfg_endian_swap_we = addr_hit[3] & reg_we & ~wr_err;
  assign cfg_endian_swap_wd = reg_wdata[2];
  assign cfg_endian_swap_re = addr_hit[3] && reg_re;

  assign cfg_digest_swap_we = addr_hit[3] & reg_we & ~wr_err;
  assign cfg_digest_swap_wd = reg_wdata[3];
  assign cfg_digest_swap_re = addr_hit[3] && reg_re;

  assign cfg_mode_we = addr_hit[3] & reg_we & ~wr_err;
  assign cfg_mode_wd = reg_wdata[5:4];
  assign cfg_mode_re = addr_hit[3] && reg_re;

  assign cfg_data_width_we = addr_hit[3] & reg_we & ~wr_err;
  assign cfg_data_width_wd = reg_wdata[9:8];
  assign cfg_data_width_re = addr_hit[3] && reg_re;

  assign cmd_start_we = addr_hit[4] & reg_we & ~wr_err;
  assign cmd_start_wd = reg_wdata[0];

  assign cmd_process_we = addr_hit[4] & reg_we & ~wr_err;
  assign cmd_process_wd = reg_wdata[1];

  assign status_fifo_empty_re = addr_hit[5] && reg_re;

  assign status_fifo_full_re = addr_hit[5] && reg_re;

  assign status_fifo_depth_re = addr_hit[5] && reg_re;

  assign dummy_we = addr_hit[6] & reg_we & ~wr_err;
  assign dummy_wd = reg_wdata[31:0];

  assign alert_test_data_parity_we = addr_hit[7] & reg_we & ~wr_err;
  assign alert_test_data_parity_wd = reg_wdata[0];

  assign alert_test_sram_uncorrectable_we = addr_hit[7] & reg_we & ~wr_err;
  assign alert_test_sram_uncorrectable_wd = reg_wdata[1];


  // Read data return
  always_comb begin
    reg_rdata_next = '0;
    unique case (1'b1)
      addr_hit[0]: begin
        reg_rdata_next[0] = intr_state_kmac_done_qs;
        reg_rdata_next[1] = intr_state_fifo_empty_qs;
        reg_rdata_next[2] = intr_state_kmac_err_qs;
      end

      addr_hit[1]: begin
        reg_rdata_next[0] = intr_enable_kmac_done_qs;
        reg_rdata_next[1] = intr_enable_fifo_empty_qs;
        reg_rdata_next[2] = intr_enable_kmac_err_qs;
      end

      addr_hit[2]: begin
        reg_rdata_next[0] = '0;
        reg_rdata_next[1] = '0;
        reg_rdata_next[2] = '0;
      end

      addr_hit[3]: begin
        reg_rdata_next[0] = cfg_kmac_en_qs;
        reg_rdata_next[2] = cfg_endian_swap_qs;
        reg_rdata_next[3] = cfg_digest_swap_qs;
        reg_rdata_next[5:4] = cfg_mode_qs;
        reg_rdata_next[9:8] = cfg_data_width_qs;
      end

      addr_hit[4]: begin
        reg_rdata_next[0] = '0;
        reg_rdata_next[1] = '0;
      end

      addr_hit[5]: begin
        reg_rdata_next[0] = status_fifo_empty_qs;
        reg_rdata_next[1] = status_fifo_full_qs;
        reg_rdata_next[8:4] = status_fifo_depth_qs;
      end

      addr_hit[6]: begin
        reg_rdata_next[31:0] = dummy_qs;
      end

      addr_hit[7]: begin
        reg_rdata_next[0] = '0;
        reg_rdata_next[1] = '0;
      end

      addr_hit[8]: begin
        reg_rdata_next[31:0] = err_code_qs;
      end

      default: begin
        reg_rdata_next = '1;
      end
    endcase
  end

  // Assertions for Register Interface
  `ASSERT_PULSE(wePulse, reg_we)
  `ASSERT_PULSE(rePulse, reg_re)

  `ASSERT(reAfterRv, $rose(reg_re || reg_we) |=> tl_o.d_valid)

  `ASSERT(en2addrHit, (reg_we || reg_re) |-> $onehot0(addr_hit))

  // this is formulated as an assumption such that the FPV testbenches do disprove this
  // property by mistake
  `ASSUME(reqParity, tl_reg_h2d.a_valid |-> tl_reg_h2d.a_user.parity_en == 1'b0)

endmodule
